def karatsuba(x, y):
 # Caso base: se x ou y for menor que 10, usa a multiplicação direta
 if x < 10 or y < 10:
     return x * y

 # Determina o tamanho dos números
 n = max(len(str(x)), len(str(y)))
 m = n // 2

 # Divide os números em duas partes
 high_x, low_x = divmod(x, 10**m)
 high_y, low_y = divmod(y, 10**m)

 # Recursivamente calcula os três produtos necessários
 z0 = karatsuba(low_x, low_y)            # Produto das partes baixas
 z1 = karatsuba((low_x + high_x), (low_y + high_y))  # Produto cruzado
 z2 = karatsuba(high_x, high_y)         # Produto das partes altas

 # Combina os resultados usando a fórmula de Karatsuba
 return (z2 * 10**(2*m)) + ((z1 - z2 - z0) * 10**m) + z0

# Teste do programa
if __name__ == "__main__":
 num1 = int(input("Digite o primeiro número: "))
 num2 = int(input("Digite o segundo número: "))

 resultado = karatsuba(num1, num2)
 print(f"O resultado da multiplicação de {num1} e {num2} é: {resultado}")